import printf from "libc";

extern printf(fmt: *u8, ...);

frame main() {
    local i: u64 = 1;
    # Basic loop (while i <= 3)
    loop {
        if i > 3 { break; }
        call printf("%d\n", i);
        i = i + 1;
    }

    # Loop with continue
    local j: u64 = 7;
    loop {
        if j > 9 { break; }
        if j % 2 == 0 {
            j = j + 1;
            continue;
        }
        call printf("%d\n", j);
        j = j + 1;
    }

    # Infinite loop
    loop {
        call printf("loop\n");
        break;
    }
}

