frame plus(a: u64, b: u64) ret u64 {
    return a + b;
}

frame plusPlus(a: u64, b: u64, c: u64) ret u64 {
    return call plus(a, call plus(b, c));
}

frame main() {
    local res: u64 = call plus(1, 2);
    call printf("1+2 = %d\n", res);

    res = call plusPlus(1, 2, 3);
    call printf("1+2+3 = %d\n", res);
}

