struct Rect {
    width: u64,
    height: u64,

    frame area() ret u64 {
        return this.width * this.height;
    }

    frame perim() ret u64 {
        return 2 * this.width + 2 * this.height;
    }

    frame scale(factor: u64) {
        this.width = this.width * factor;
        this.height = this.height * factor;
    }
}

frame main() {
    local r: Rect;
    r.width = 10;
    r.height = 5;

    call printf("area: %d\n", call r.area());
    call printf("perim: %d\n", call r.perim());

    call r.scale(2);
    call printf("after scale - area: %d\n", call r.area());
}

