struct Box<T> {
    val: T,

    frame get() ret T {
        return this.val;
    }

    frame set(value: T) {
        this.val = value;
    }
}

struct Pair<A, B> {
    first: A,
    second: B,

    frame getFirst() ret A {
        return this.first;
    }

    frame getSecond() ret B {
        return this.second;
    }
}

frame main() {
    local intBox: Box<u64>;
    call intBox.set(123);
    call printf("Box<u64>: %d\n", call intBox.get());

    local strBox: Box<*u8>;
    call strBox.set("Generic");
    call printf("Box<*u8>: %s\n", call strBox.get());

    local p: Pair<u64, *u8>;
    p.first = 42;
    p.second = "hello";
    call printf("Pair: %d, %s\n", call p.getFirst(), call p.getSecond());
}

