struct Result {
    val: u64,
    err: u64, # 0 = ok, 1 = error
}

frame f(arg: u64) ret Result {
    local res: Result;
    if arg == 42 {
        res.err = 1;
        return res;
    }
    res.val = arg + 3;
    res.err = 0;
    return res;
}

frame main() {
    local r: Result = call f(42);
    if r.err != 0 {
        call printf("f failed\n");
    } else {
        call printf("f worked: %d\n", r.val);
    }

    r = call f(7);
    if r.err != 0 {
        call printf("f failed\n");
    } else {
        call printf("f worked: %d\n", r.val);
    }
}

