frame sum(count: u64, ...:u64) ret u64 {
    local total: u64 = 0;
    local i: u64 = 0;
    loop {
        if i >= count { break; }
        total = total + args[i];
        i = i + 1;
    }
    return total;
}

frame main() {
    call printf("%d\n", call sum(2, 1, 2));
    call printf("%d\n", call sum(3, 1, 2, 3));
    local nums: u64[4] = [1, 2, 3, 4];
    call printf("%d\n", call sum(4, nums[0], nums[1], nums[2], nums[3]));
}

import printf from "libc";

extern printf(fmt: *u8, ...);

frame main() {
    local arr: u64[5] = [4, 2, 5, 1, 3];
    local i: u64 = 0;
    local j: u64 = 0;
    local temp: u64 = 0;

    # Bubble sort
    loop {
        if i >= 5 { break; }
        j = 0;
        loop {
            if j >= 4 - i { break; }
            if arr[j] > arr[j+1] {
                temp = arr[j];
                arr[j] = arr[j+1];
                arr[j+1] = temp;
            }
            j = j + 1;
        }
        i = i + 1;
    }

    call printf("Sorted: %d %d %d %d %d\n", arr[0], arr[1], arr[2], arr[3], arr[4]);
}

