import printf, fopen, fwrite, fclose from "libc";

extern printf(fmt: *u8, ...);
extern fopen(filename: *u8, mode: *u8) ret *u8;
extern fwrite(ptr: *u8, size: u64, nmemb: u64, stream: *u8) ret u64;
