import printf, fopen, fread, fclose from "libc";

extern printf(fmt: *u8, ...);
extern fopen(filename: *u8, mode: *u8) ret *u8;
extern fread(ptr: *u8, size: u64, nmemb: u64, stream: *u8) ret u64;
extern fclose(stream: *u8) ret i32;

frame main() ret u8 {
    local f: *u8 = call fopen("/tmp/output.txt", "r");
    if f == NULL {
        call printf("Failed to open file (make sure /tmp/output.txt exists)\n");
        return 1;
    }

    local buf: u8[100];
    local n: u64 = call fread(buf, 1, 99, f);
    buf[n] = 0;
    call printf("Read %d bytes: %s\n", n, buf);

    call fclose(f);
    return 0;
}

